import TreeItem from './TreeItem';
export default {
  name: "vTree",
  props: {
    url: String,
    isEdit: {
      type: Boolean,
      default: false
    },
    treeData: {
      type: Array
    }
  },
  data: function data() {
    return {
      db: [],
      nodes: {
        opened: null,
        selected: {
          isSelected: false
        }
      }
    };
  },
  components: {
    TreeItem: TreeItem
  },
  created: function created() {
    if (this.treeData && this.treeData.length > 0) {
      this.db = this.treeData;
      return;
    }

    this.getData();
  },
  methods: {
    getData: function getData() {
      var _this = this;

      this.$http.get(this.url, {
        params: {
          pid: 0
        }
      }).then(function (resp) {
        _this.db = resp.data;

        _this.db.forEach(function (n) {
          return n['path'] = [n.name];
        });
      });
    },
    handleAdd: function handleAdd(node) {
      this.$emit("handleAdd", this.copyNodeInfo(node));
    },
    handleEdit: function handleEdit(id, name) {
      this.$emit("handleEdit", id, name);
    },
    handleDelete: function handleDelete(id) {
      this.deleteById(id, this.db);
      this.$emit("handleDelete", id);
    },
    handleClick: function handleClick(node) {
      this.$emit("handleClick", this.copyNodeInfo(node));
    },
    // 根据id删除
    deleteById: function deleteById(id, arr) {
      var src = arr || this.db;

      for (var i in src) {
        var d = src[i];

        if (d.id === id) {
          src.splice(i, 1);
          return;
        }

        if (d.children && d.children.length > 0) {
          this.deleteById(id, d.children);
        }
      }
    },
    copyNodeInfo: function copyNodeInfo(node) {
      var o = {};

      for (var i in node) {
        o[i] = node[i];
      }

      return o;
    }
  },
  watch: {}
};
